import React, { useState } from 'react'
import { useEffect } from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios'
export default function Instructor() {
    const [instructor_info, setInstructor_info] = useState([]);
    const [instructor_course, setInstructor_course] = useState([]);
    const [course_prev, setCourse_prev] = useState([]);
    const { instructor_id } = useParams()


    useEffect(() => {
        axios.get('http://localhost:5000/instructor/' + instructor_id)
            .then(res => {
                console.log(res.data)
                setInstructor_course(res.data.instructor_course)
                setInstructor_info(res.data.instructor_info)
                setCourse_prev(res.data.course_prev)

            })
            .catch(err => {
                console.log(err)
            })
    }, [])

    return (
        <div className="instructor">
            <h1>Instructor</h1>
            <>
                {instructor_info.map((item, index) => {
                    return <>{item.name}
                    // in next line
                    {"\n"}{item.id}

                    {"\n"}{item.dept_name}</>
                })}
            </>

            <>
                {instructor_course.map((item, index) => {
                    return <a href={"/course/" + item.course_id}>
                        {item.course_id}
                    </a>

                }
                )}
            </>
            <>
            <p>
                Previous Courses:

            </p>
                {course_prev.map((item, index) => {
                    //return table
                    return <table>
                        <tr>
                            <th>Course ID</th>
                            <th>year</th>
                            <th>semester</th>
                        </tr>
                        <tr>
                            <td>
                                <a href={"/course/" + item.course_id}>
                                    {item.course_id}
                                </a>
                            </td>

                            <td>{item.year}</td>
                            <td>{item.semester}</td>
                        </tr>
                    </table>
                })}
            </>








        </div>

    )
}
