import React, { Fragment } from "react";
import { useState, useEffect } from "react";
import axios from "axios";


export default function Home() {
  const [user_info, setUser_info] = useState([]);
  const [semesters, setSemesters] = useState([]);
  const [first_semester, setFirst_semester] = useState([]);
  const [remianing, setRemianing] = useState([]);
  const [fs_courses, setFs_courses] = useState([]);

  //handle submit for drop button
  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/drop", {
        year: first_semester.year,
        semester: first_semester.semester,
        course_id: event.target.value,
      }, { withCredentials: true });
      if (response.status === 200) {
        window.location.href = "/home";
      }
    } catch (e) {
      console.log("drop failed. Please try again.")
    }
  };





  useEffect(() => {
    axios.get("http://localhost:5000/home", { withCredentials: true })
      .then((res) => {
        setUser_info(res.data.user_info);
        setSemesters(res.data.semesters);
        setFirst_semester(res.data.semesters[0]);
        setFs_courses(res.data.semesters[0].courses);
        setRemianing(res.data.semesters.slice(1));
      });
  }
    , []);
  return (
    <div className="home">
      <h1>Home</h1>

      <>
        {user_info.map((item, index) => {
          return <>{item.id}</>;
        })}

      </>
      <>
        {user_info.map((item, index) => {
          return <>{item.name}</>;
        })}
      </>
      <>
        {user_info.map((item, index) => {
          return <>{item.dept_name}</>;
        })}
      </>
      <>
        {user_info.map((item, index) => {
          return <>{item.tot_cred}</>;
        })}
      </>

      <p>

          current semester: {first_semester.year}{""}{first_semester.semester}

        <table>
          <tr>
            <th>Course ID</th>
            <th>DROP</th>
          </tr>
          {fs_courses.map((item, index) => {
            return <tr>
              <td><a href={"/course/" + item.course_id}>
                {item.course_id}
              </a></td>
              <td>
                <button onClick={handleSubmit} value={(first_semester.year,first_semester.semester,item.course_id)}>DROP</button>
              </td>
            </tr>
          })}

        </table>
      </p>

      <>
        {remianing.map((item, index) => {

          return <table>

            <h3>
              {item.year}{""}{item.semester}
            </h3>

            <tr>
              <th>Course ID</th>
              <th>Grade</th>
            </tr>


            {item.courses.map((item, index) => {
              return <tr>
                <td><a href={"/course/" + item.course_id}>
                  {item.course_id}
                </a></td>
                <td>{item.grade}</td>
              </tr>
            })}

          </table>


        })}
      </>


    </div>


  );
}
