import React, { Fragment } from "react";
import { useState, useEffect } from "react";
import axios from "axios";


export default function Home() {
  const [user_info, setUser_info] = useState([]);
  const [semesters, setSemesters] = useState([]);
  useEffect(() => {
    axios.get("http://localhost:5000/home", { withCredentials: true })
      .then((res) => {
        setUser_info(res.data.user_info);
        setSemesters(res.data.semesters);
      });
      const first_semester = semesters[0];
      console.log("bsbdbfh", first_semester);
      const remianing = semesters.slice(1);
  }
    , []);
  return (
    <div className="home">
      <h1>Home</h1>

      <>
        {user_info.map((item, index) => {
          return <>{item.id}</>;
        })}

      </>
      <>
        {user_info.map((item, index) => {
          return <>{item.name}</>;
        })}
      </>
      <>
        {user_info.map((item, index) => {
          return <>{item.dept_name}</>;
        })}
      </>
      <>
        {user_info.map((item, index) => {
          return <>{item.tot_cred}</>;
        })}
      </>

      <p>
        <h3>
          current semester: {first_semester.year}{""}{first_semester.semester}
        </h3>
        <table>
          <tr>
            <th>Course ID</th>
            <th>DROP</th>
          </tr>
          {first_semester.courses.map((item, index) => {
            return <tr>
              <td><a href={"/course/" + item.course_id}>
                {item.course_id}
              </a></td>
              <td><button>Drop</button></td>
            </tr>
          })}

        </table>
      </p>

      <>
        {remianing.map((item, index) => {

          return <table>

            <h3>
              {item.year}{""}{item.semester}
            </h3>

            <tr>
              <th>Course ID</th>
              <th>Grade</th>
            </tr>


            {item.courses.map((item, index) => {
              return <tr>
                <td><a href={"/course/" + item.course_id}>
                  {item.course_id}
                </a></td>
                <td>{item.grade}</td>
              </tr>
            })}

          </table>


        })}
      </>


    </div>


  );
}
