import React, { useState } from 'react'
import { useEffect } from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios'
export default function Course() {
    const [course_info, setCourse_info] = useState([]);
    const [course_prereq, setCourse_prereq] = useState([]);
    const [course_instructor, setCourse_instructor] = useState([]);
    const { course_id } = useParams()


    useEffect(() => {
        axios.get('http://localhost:5000/course/' + course_id)
            .then(res => {
                console.log(res.data)
                setCourse_info(res.data.course_info)
                setCourse_prereq(res.data.course_prereq)
                setCourse_instructor(res.data.course_instructor)
            })
            .catch(err => {
                console.log(err)
            })
    }, [])

    return (
        <div className="course">
            <h1>Course</h1>
            <>
                {course_info.map((item, index) => {
                    return <>{item.course_id}</>
                    //go to next line
                })}

            </>
            <>
                {course_info.map((item, index) => {
                    return <>{item.title}</>

                })}
            </>
            <>
                {course_info.map((item, index) => {
                    return <>{item.dept_name}</>

                })}
            </>

            <>
                {course_info.map((item, index) => {
                    return <>{item.credits}</>

                })}
            </>
            PRE-REQUISITES
            <>
                {course_prereq.map((item, index) => {
                    return <a href={"/course/" + item.prereq_id}>
                        {item.prereq_id}
                    </a>
                })}
            </>

            INSTRUCTOR
            <>
                {course_instructor.map((item, index) => {
                //return table
                <table>
                    <thead>
                        <tr>
                            <th>Instructor ID</th>
                            <th>Name</th>
                            <th>year</th>
                            <th>semester</th>

                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>{item.id}</td>
                            <td>{item.name}</td>
                            <td>{item.year}</td>
                            <td>{item.semester}</td>
                        </tr>
                    </tbody>


                </table>
                })}

            </>
        </div>

    )



}
