import React, { useState } from 'react'
import { useEffect } from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios'
export default function Course() {
    const [data, setData] = useState()
    const [course_info, setCourse_info] = useState([]);
    const [course_params, setCourse_params] = useState([]);
    const { course_id } = useParams()

    useEffect(() => {
        axios.get('http://localhost:5000/course/' + course_id)
            .then(res => {
                console.log(res.data)
                setCourse_info(res.data.course_info)
                setCourse_params(res.data.course_params)
            })
            .catch(err => {
                console.log(err)
            })
    }, [])

    return (
        <div className="course">
            <h1>Course</h1>
            <>
                {course_info.map((item, index) => {
                    return <>{item.course_id}</>
                    //go to next line
                })}

            </>
            <>
                {course_info.map((item, index) => {
                    return <>{item.title}</>

                })}
            </>
            <>
                {course_info.map((item, index) => {
                    return <>{item.dept_name}</>

                })}
            </>

            <>
                {course_info.map((item, index) => {
                    return <>{item.credits}</>

                })}
            </>
            <ul>
                {course_params.map((item, index) => {
                    return <li>{item.prereq_id}</li>
                })}
            </ul>





        </div>

    )



}
